import React, { useState } from 'react';

const Accordian = ({title, content, button_class_name = "accbutton", title_class_name = "acctitle", content_class_name}) =>
{
    const [visible, SetVisible] = useState(false);

    return(
        <div onClick = {() => SetVisible(!visible)} className = "home-page-second-card">
            <div className = "home-page-second-card-title">
                <div className = "home-page-second-card-title-text">
                    {title}
                </div>
                <div className = "home-page-second-card-button">
                    {visible ? '-' : '+'}
                </div>
                {visible && <div className = "home-page-second-card-content">{content}</div>}
            </div>
        </div>
    )
}

export default Accordian
