import React, { useState } from 'react';
import "./Register.css";
import emailjs from "emailjs-com";

function Redirect()
{
    window.location.href = "/thank-you";
}

window.addEventListener("load", function() {
    const form = document.getElementById("sheets-form");
    form.addEventListener("submit", function(e) {
      e.preventDefault();
      const data = new FormData(form);
      const action = e.target.action;
      fetch(action, {
        method: 'POST',
        body: data,
      })
      .then(() => {
        Redirect();
      })
    });
  });

export default function Register()
{
    const [guardian_full_name, SetGuardianFullName] = useState("");
    const [student_full_name, SetStudentFullName] = useState("");
    const [email, SetEmail] = useState("");
    const [phone_number, SetPhoneNumber] = useState("");
    const [contact_by_phone, SetContactByPhone] = useState(false);
    const [preferred_platform, SetPreferredPlatform] = useState("");
    const [preferred_location, SetPreferredLocation] = useState("");
    const [preferred_instrument, SetPreferredInstrument] = useState("");
    const [platform, SetPlatform] = useState("");
    const [lesson_times, SetLessonTimes] = useState("");

    return (
        <div id = "register-page-wrapper">
            <div id = "register-title">Register</div>
            <div id = "register-form">
                <form id = "sheets-form" method = "POST" onSubmit = {Redirect} action = "https://script.google.com/macros/s/AKfycbzbknbXB0Lv6ITPCxbyEaGUkpkiQsvOBkQ_kOUmQobDSi6Yl1i-bURMWuuV4AKKOoWM/exec">
                    <div>
                        <label htmlFor = "guardian-full-name-field">Guardian Full Name</label>
                        <br />
                        <input id = "guardian-full-name-field" className = "registration-wide-input-field" name = "Guardian Full Name" type = "text" value = {guardian_full_name} onChange = {(e) => SetGuardianFullName(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "student-full-name-field">Student Full Name</label>
                        <br />
                        <input name = "Student Full Name" id = "student-full-name-field" className = "registration-wide-input-field" type = "text" value = {student_full_name} onChange = {(e) => SetStudentFullName(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "email-field">Email</label>
                        <br />
                        <input name = "Email" id = "email-field" className = "registration-wide-input-field" type = "text" value = {email} onChange = {(e) => SetEmail(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "phone-number-field">Phone Number (optional)</label>
                        <br />
                        <input name = "Phone Number" id = "phone-number-field" className = "registration-wide-input-field" type = "text" value = {phone_number} onChange = {(e) => SetPhoneNumber(e.target.value)} />
                    </div>
                    <div>
                        <input name = "Contact by PN?" id = "phone-checkbox" type = "checkbox" value = {contact_by_phone} onChange = {(e) => SetContactByPhone(!contact_by_phone)} />
                        <label htmlFor = "phone-checkbox"> I would prefer to be contacted by phone</label>
                        <br />
                        <br />
                    </div>
                    <div>
                        <label htmlFor = "preferred-platform-field">Preferred Lesson Platform (Discord, Zoom, or any preferred platform)</label>
                        <br />
                        <input name = "Lesson Platform" id = "preferred-platform-field" className = "registration-wide-input-field" type = "text" value = {preferred_platform} onChange = {(e) => SetPreferredPlatform(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "preferred-location-field">Would you prefer to have your lessons in person or online? &sup1;</label>
                        <br />
                        <select name = "Online?" id = "preferred-location-field" className = "registration-wide-input-field" value = {preferred_location} onChange = {(e) => SetPreferredLocation(e.target.value)}>
                            <option value = "">Select an option</option>
                            <option value = "in_person">In person</option>
                            <option value = "online">Online</option>
                            <option value = "either">Either is fine</option>
                        </select>
                    </div>
                    <div>
                        <label htmlFor = "preferred-instrument-field">Preferred Instrument &sup2;</label>
                        <select id = "preferred-instrument-field" name = "Preferred Instrument" className = "registration-wide-input-field" value = {preferred_instrument} required="" onChange = {(e) => SetPreferredInstrument(e.target.value)}>
                            <option value="">Select an option</option>
                            <option value="viola">Viola</option>
                            <option value="violin">Violin</option>
                            <option value="piano">Piano</option>
                            <option value="guitar">Guitar</option>
                            <option value="cello">Cello</option>
                            <option value="trombone">Trombone</option>
                            <option value="theory">Music Theory</option>
                        </select>
                    </div>
                    <div>
                        <label htmlFor = "platform-field">How did you find out about us?</label>
                        <select id = "platform-field" name = "Found out from" className = "registration-wide-input-field" value = {platform} onChange = {(e) => SetPlatform(e.target.value)}>
                            <option value="">Select an option</option>
                            <option value="kijiji">Kijiji</option>
                            <option value="social_media">Social Media</option>
                            <option value="friends_or_family">Friends/Family</option>
                            <option value="news">News</option>
                            <option value="google">Google</option>
                            <option value="other">Other</option>
                        </select>
                    </div>
                    <div>
                        <label htmlFor = "lesson-times-field">Please state lesson times including days of the week and times of day</label>
                        <br />
                        <textarea name = "Statement" id = "lesson-times-field" className = "registration-wide-input-field" value = {lesson_times} onChange = {(e) => SetLessonTimes(e.target.value)} />
                    </div>
                    <div>
                        * Note: all fields are required except phone number
                        <br />
                        <br />
                        &sup1;
                         Note: If you select "either is fine," you will be more likely to be matched up. Additonally, in person lessons will be held at either the student's household or the teacher's. If in person is selected, the location will be decided by the student and the teacher together.
                        <br />
                    </div>
                    <br />
                    <input id = "submit-button" type = "submit" />
                </form>
            </div>
        </div>
    )
}
