import React, { useState } from 'react';
import "./Register.css";

function CheckFormValidity(FormArray)
{
    for (var answer of FormArray)
    {
        if (answer == "")
        {
            return false;
        }
    }
}

function HandleRegistration(FormArray)
{
    if (!CheckFormValidity(FormArray))
    {
        alert("Please fill all required fields")
        return;
    }

}

export default function Register()
{
    const [guardian_full_name, SetGuardianFullName] = useState("");
    const [student_full_name, SetStudentFullName] = useState("");
    const [email, SetEmail] = useState("");
    const [phone_number, SetPhoneNumber] = useState("");
    const [contact_by_phone, SetContactByPhone] = useState(false);
    const [preferred_platform, SetPreferredPlatform] = useState("");
    const [preferred_instrument, SetPreferredInstrument] = useState("");
    const [platform, SetPlatform] = useState("");
    const [lesson_times, SetLessonTimes] = useState("");

    function HandleSubmit(event)
    {
        event.preventDefault();
        const FormArray = [guardian_full_name, student_full_name, email, phone_number, contact_by_phone, preferred_platform, preferred_instrument, platform, lesson_times];
        HandleRegistration(FormArray);
    }

    return (
        <div id = "register-page-wrapper">
            <div id = "register-title">Register</div>
            <div id = "register-form">
                <form onSubmit = {HandleSubmit}>
                    <div>
                        <label htmlFor = "guardian-full-name-field">Guardian Full Name</label>
                        <br />
                        <input id = "guardian-full-name-field" className = "registration-wide-input-field" type = "text" value = {guardian_full_name} onChange = {(e) => SetGuardianFullName(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "student-full-name-field">Student Full Name</label>
                        <br />
                        <input id = "student-full-name-field" className = "registration-wide-input-field" type = "text" value = {student_full_name} onChange = {(e) => SetStudentFullName(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "email-field">Email</label>
                        <br />
                        <input id = "email-field" className = "registration-wide-input-field" type = "text" value = {email} onChange = {(e) => SetEmail(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "phone-number-field">Phone Number</label>
                        <br />
                        <input id = "phone-number-field" className = "registration-wide-input-field" type = "text" value = {phone_number} onChange = {(e) => SetPhoneNumber(e.target.value)} />
                    </div>
                    <div>
                        <input id = "phone-checkbox" type = "checkbox" value = {contact_by_phone} onChange = {(e) => SetContactByPhone(!contact_by_phone)} />
                        <label htmlFor = "phone-checkbox"> I would prefer to be contacted by phone</label>
                        <br />
                        <br />
                    </div>
                    <div>
                        <label htmlFor = "preferred-platform-field">Preferred Lesson Platform (Discord, Zoom, or any preferred platform)</label>
                        <br />
                        <input id = "preferred-platform-field" className = "registration-wide-input-field" type = "text" value = {preferred_platform} onChange = {(e) => SetPreferredPlatform(e.target.value)} />
                    </div>
                    <div>
                        <label htmlFor = "preferred-instrument-field">Preferred Instrument -- Piano is currently full. If registering for piano, you may not be assigned a teacher until a spot is available.</label>
                        <select className = "registration-wide-input-field" value = {preferred_instrument} required="" onChange = {(e) => SetPreferredInstrument(e.target.value)}>
                            <option value="">Select an option</option>
                            <option value="viola">Viola</option>
                            <option value="violin">Violin</option>
                            <option value="piano">Piano</option>
                            <option value="guitar">Guitar</option>
                            <option value="cello">Cello</option>
                            <option value="trombone">Trombone</option>
                            <option value="theory">Music Theory</option>
                        </select>
                    </div>
                    <div>
                        <label htmlFor = "preferred-instrument-field">How did you find out about us?</label>
                        <select className = "registration-wide-input-field" value = {platform} onChange = {(e) => SetPlatform(e.target.value)}>
                            <option value="">Select an option</option>
                            <option value="viola">Viola</option>
                            <option value="violin">Violin</option>
                            <option value="piano">Piano</option>
                            <option value="guitar">Guitar</option>
                            <option value="cello">Cello</option>
                            <option value="trombone">Trombone</option>
                            <option value="theory">Music Theory</option>
                        </select>
                    </div>
                    <div>
                        <label htmlFor = "lesson-times-field">Please state lesson times including days of the week and times of day</label>
                        <br />
                        <textarea id = "lesson-times-field" className = "registration-wide-input-field" value = {lesson_times} onChange = {(e) => SetLessonTimes(e.target.value)} />
                    </div>
                    <input id = "submit-button" type = "submit" />
                </form>
            </div>
        </div>
    )
}
