import React, { useState } from 'react';

const Accordian = ({title, content, button_class_name = "acc-button", title_class_name = "acc-title", content_class_name = 'acc-content' }) =>
{
    const [visible, SetVisible] = useState(false);

    return(
        <div onClick = {() => SetVisible(!visible)} className = "home-page-second-card">
            <div className = {title_class_name}>
                {title}
            </div>
            <div className = {button_class_name}>
                {visible ? '-' : '+'}
            </div>
            {visible && <div className = {content_class_name}>{content}</div>}
        </div>
    )
}

export default Accordian
